import { computed, watch } from '@vue/composition-api';
import store from '@/store';
export default function usAppConfig() {
  // ------------------------------------------------
  // isVerticalMenuCollapsed
  // ------------------------------------------------
  var isVerticalMenuCollapsed = computed({
    get: function get() {
      return store.state.verticalMenu.isVerticalMenuCollapsed;
    },
    set: function set(val) {
      store.commit('verticalMenu/UPDATE_VERTICAL_MENU_COLLAPSED', val);
    }
  }); // ------------------------------------------------
  // RTL
  // ------------------------------------------------

  var isRTL = computed({
    get: function get() {
      return store.state.appConfig.layout.isRTL;
    },
    set: function set(val) {
      store.commit('appConfig/TOGGLE_RTL', val);
    }
  }); // ------------------------------------------------
  // Skin
  // ------------------------------------------------

  var skin = computed({
    get: function get() {
      return store.state.appConfig.layout.skin;
    },
    set: function set(val) {
      store.commit('appConfig/UPDATE_SKIN', val);
    }
  });
  var skinClasses = computed(function () {
    if (skin.value === 'bordered') return 'bordered-layout';
    if (skin.value === 'semi-dark') return 'semi-dark-layout'; // Do not return any class for dark layout because dark layout updates class in body
    // Do not return any class for light layout as that is default layout

    return null;
  }); // ------------------------------------------------
  // routerTransition
  // ------------------------------------------------

  var routerTransition = computed({
    get: function get() {
      return store.state.appConfig.layout.routerTransition;
    },
    set: function set(val) {
      store.commit('appConfig/UPDATE_ROUTER_TRANSITION', val);
    }
  }); // *===============================================---*
  // *--------- LAYOUT ---------------------------------------*
  // *===============================================---*
  // ------------------------------------------------
  // layoutType
  // ------------------------------------------------

  var layoutType = computed({
    get: function get() {
      return store.state.appConfig.layout.type;
    },
    set: function set(val) {
      store.commit('appConfig/UPDATE_LAYOUT_TYPE', val);
    }
  }); // Reset skin if skin is semi-dark and move to horizontal layout

  watch(layoutType, function (val) {
    if (val === 'horizontal' && skin.value === 'semi-dark') skin.value = 'light';
  }); // ------------------------------------------------
  // Content Width (Full/Boxed)
  // ------------------------------------------------

  var contentWidth = computed({
    get: function get() {
      return store.state.appConfig.layout.contentWidth;
    },
    set: function set(val) {
      store.commit('appConfig/UPDATE_CONTENT_WIDTH', val);
    }
  }); // ------------------------------------------------
  // isNavMenuHidden
  // ------------------------------------------------

  var isNavMenuHidden = computed({
    get: function get() {
      return store.state.appConfig.layout.menu.hidden;
    },
    set: function set(val) {
      store.commit('appConfig/UPDATE_NAV_MENU_HIDDEN', val);
    }
  }); // *===============================================---*
  // *--------- NAVBAR ---------------------------------------*
  // *===============================================---*

  var navbarBackgroundColor = computed({
    get: function get() {
      return store.state.appConfig.layout.navbar.backgroundColor;
    },
    set: function set(val) {
      store.commit('appConfig/UPDATE_NAVBAR_CONFIG', {
        backgroundColor: val
      });
    }
  });
  var navbarType = computed({
    get: function get() {
      return store.state.appConfig.layout.navbar.type;
    },
    set: function set(val) {
      store.commit('appConfig/UPDATE_NAVBAR_CONFIG', {
        type: val
      });
    }
  }); // *===============================================---*
  // *--------- FOOTER ---------------------------------------*
  // *===============================================---*

  var footerType = computed({
    get: function get() {
      return store.state.appConfig.layout.footer.type;
    },
    set: function set(val) {
      store.commit('appConfig/UPDATE_FOOTER_CONFIG', {
        type: val
      });
    }
  });
  return {
    isVerticalMenuCollapsed: isVerticalMenuCollapsed,
    isRTL: isRTL,
    skin: skin,
    skinClasses: skinClasses,
    routerTransition: routerTransition,
    // Navbar
    navbarBackgroundColor: navbarBackgroundColor,
    navbarType: navbarType,
    // Footer
    footerType: footerType,
    // Layout
    layoutType: layoutType,
    contentWidth: contentWidth,
    isNavMenuHidden: isNavMenuHidden
  };
}
