import { BCard, BCardHeader, BCardTitle, BCardBody, BMediaBody, BMedia, BMediaAside, BAvatar, BButton, VBTooltip } from 'bootstrap-vue';
import { formatToken, numberWithCommas } from '@/libs/utils';
import OperationModal from '@/views/components/OperationModal/index.vue';
export default {
  components: {
    BButton,
    BCard,
    BCardHeader,
    BCardTitle,
    BCardBody,
    BMediaBody,
    BMedia,
    BMediaAside,
    BAvatar,
    OperationModal
  },
  directives: {
    'b-tooltip': VBTooltip
  },
  props: {
    data: {
      type: Object,
      required: true
    },
    validator: {
      type: String,
      required: true
    },
    address: {
      type: String,
      required: true
    }
  },

  data() {
    return {};
  },

  computed: {
    denoms() {
      return this.$store.state.chains.denoms;
    }

  },
  methods: {
    formatNumber(value) {
      if (value < 1) return value;

      if (value.indexOf('.') > 0) {
        // eslint-disable-next-line no-undef
        return numberWithCommas(BigInt(value.substring(0, value.indexOf('.'))));
      } // eslint-disable-next-line no-undef


      return numberWithCommas(BigInt(Number(value).toFixed(0)));
    },

    formatDenom(value) {
      return formatToken(value, this.denoms, 2);
    }

  }
};
