import { BFormInput, BFormText } from 'bootstrap-vue';
import { ref } from '@vue/composition-api';
import { title } from '@core/utils/filter';
import store from '@/store';
export default {
  components: {
    BFormInput: BFormInput,
    BFormText: BFormText
  },
  setup: function setup() {
    var showSearchBar = ref(false);
    var perfectScrollbarSettings = {
      maxScrollbarLength: 60
    };
    return {
      showSearchBar: showSearchBar,
      perfectScrollbarSettings: perfectScrollbarSettings,
      title: title
    };
  },
  data: function data() {
    return {
      searchQuery: null,
      error: null
    };
  },
  methods: {
    reset: function reset() {
      this.showSearchBar = false;
      this.searchQuery = null;
      this.error = null;
    },
    doQuery: function doQuery() {
      var height = /^\d+$/;
      var txhash = /^[A-Z\d]{64}$/;
      var addr = /^[a-z]+1[a-z\d]{38}$/;
      var key = this.searchQuery;
      var c = store.state.chains.selected;

      if (!Object.values(this.$route.params).includes(key)) {
        if (height.test(key)) {
          this.$router.push({
            name: 'block',
            params: {
              chain: c.chain_name,
              height: key
            }
          });
        } else if (txhash.test(key)) {
          this.$router.push({
            name: 'transaction',
            params: {
              chain: c.chain_name,
              hash: key
            }
          });
        } else if (addr.test(key)) {
          this.$router.push({
            name: 'chain-account',
            params: {
              chain: c.chain_name,
              address: key
            }
          });
        } else {
          this.error = 'The input not recognized';
        }
      } // this.$router.push('/')

    }
  }
};
