import { BCard, BTableSimple, BTr, BTd, BBadge, BCardBody, BAlert, BCardTitle } from 'bootstrap-vue';
import { toDay, tokenFormatter } from '@/libs/utils';
import ObjectFieldComponent from './components/ObjectFieldComponent.vue';
export default {
  components: {
    BAlert,
    BCard,
    BCardTitle,
    BCardBody,
    BTableSimple,
    BTr,
    BTd,
    BBadge,
    ObjectFieldComponent
  },

  beforeRouteUpdate(to, from, next) {
    const {
      hash
    } = to.params;

    if (hash !== from.params.hash) {
      this.error = null;
      this.$http.getTxs(hash).then(res => {
        this.tx = res;
      }).catch(err => {
        this.error = err;
      });
      next();
    }
  },

  data() {
    return {
      error: null,
      tx: {
        tx: {}
      }
    };
  },

  created() {
    const {
      hash
    } = this.$route.params;
    this.$http.getTxs(hash).then(res => {
      this.error = null;
      this.tx = res;
    }).catch(err => {
      this.error = err;
    });
  },

  methods: {
    formattoken: v => tokenFormatter(v),
    formatTime: v => toDay(v)
  }
};
