import { BFormInput, BFormText } from 'bootstrap-vue';
import { ref } from '@vue/composition-api';
import { title } from '@core/utils/filter';
import store from '@/store';
export default {
  components: {
    BFormInput,
    BFormText
  },

  setup() {
    const showSearchBar = ref(false);
    const perfectScrollbarSettings = {
      maxScrollbarLength: 60
    };
    return {
      showSearchBar,
      perfectScrollbarSettings,
      title
    };
  },

  data() {
    return {
      searchQuery: null,
      error: null
    };
  },

  methods: {
    reset() {
      this.showSearchBar = false;
      this.searchQuery = null;
      this.error = null;
    },

    doQuery() {
      const height = /^\d+$/;
      const txhash = /^[A-Z\d]{64}$/;
      const addr = /^[a-z]+1[a-z\d]{38}$/;
      const key = this.searchQuery;
      const c = store.state.chains.selected;

      if (!Object.values(this.$route.params).includes(key)) {
        if (height.test(key)) {
          this.$router.push({
            name: 'block',
            params: {
              chain: c.chain_name,
              height: key
            }
          });
        } else if (txhash.test(key)) {
          this.$router.push({
            name: 'transaction',
            params: {
              chain: c.chain_name,
              hash: key
            }
          });
        } else if (addr.test(key)) {
          this.$router.push({
            name: 'chain-account',
            params: {
              chain: c.chain_name,
              address: key
            }
          });
        } else {
          this.error = 'The input not recognized';
        }
      } // this.$router.push('/')

    }

  }
};
