import { BCard, BCardBody, BAvatar, VBTooltip } from 'bootstrap-vue';
import { getUserCurrency, getUserCurrencySign } from '@/libs/utils';
export default {
  components: {
    BCard,
    BCardBody,
    BAvatar,
    VBTooltip
  },
  directives: {
    'b-tooltip': VBTooltip
  },
  props: {
    icon: {
      type: String,
      required: true
    },
    statistic: {
      type: [Number, String],
      required: true
    },
    statisticTitle: {
      type: String,
      default: ''
    },
    color: {
      type: String,
      default: 'primary'
    }
  },

  data() {
    return {
      changes: 0
    };
  },

  methods: {
    showPrice(v, statisticTitle) {
      const token = String(v).split(' ');

      if (token.length >= 2) {
        const quote = this.$store.state.chains.quotes[token[1]];

        if (quote) {
          const price = quote[getUserCurrency()];
          this.changes = quote[`${getUserCurrency()}_24h_change`];
          return `${getUserCurrencySign()}${(Number(token[0].replaceAll(',', '')) * price).toFixed(2)}`;
        }
      }

      return statisticTitle;
    }

  }
};
