import { BCard, BCardBody, BAvatar, VBTooltip } from 'bootstrap-vue';
import { getUserCurrency, getUserCurrencySign } from '@/libs/utils';
export default {
  components: {
    BCard: BCard,
    BCardBody: BCardBody,
    BAvatar: BAvatar,
    VBTooltip: VBTooltip
  },
  directives: {
    'b-tooltip': VBTooltip
  },
  props: {
    icon: {
      type: String,
      required: true
    },
    statistic: {
      type: [Number, String],
      required: true
    },
    statisticTitle: {
      type: String,
      default: ''
    },
    color: {
      type: String,
      default: 'primary'
    }
  },
  data: function data() {
    return {
      changes: 0
    };
  },
  methods: {
    showPrice: function showPrice(v, statisticTitle) {
      var token = String(v).split(' ');

      if (token.length >= 2) {
        var quote = this.$store.state.chains.quotes[token[1]];

        if (quote) {
          var price = quote[getUserCurrency()];
          this.changes = quote["".concat(getUserCurrency(), "_24h_change")];
          return "".concat(getUserCurrencySign()).concat((Number(token[0].replaceAll(',', '')) * price).toFixed(2));
        }
      }

      return statisticTitle;
    }
  }
};
