import { ValidationProvider } from 'vee-validate';
import { BRow, BCol, BInputGroup, BInputGroupAppend, BFormInput, BFormGroup, BFormSelect, BFormSelectOption, BFormText } from 'bootstrap-vue';
import { required, email, url, between, alpha, integer, password, min, digits, alphaDash, length } from '@validations';
import { formatToken, formatTokenDenom, getUnitAmount, getUserCurrency, getUserCurrencySign } from '@/libs/utils';
export default {
  name: 'TransforDialogue',
  components: {
    BRow,
    BCol,
    BInputGroup,
    BInputGroupAppend,
    BFormInput,
    BFormText,
    BFormGroup,
    BFormSelect,
    BFormSelectOption,
    ValidationProvider
  },
  props: {
    address: {
      type: String,
      default: ''
    },
    toAddress: {
      type: String,
      default: ''
    },
    balance: {
      type: Array,
      default: () => []
    }
  },

  data() {
    return {
      currency: getUserCurrency(),
      currencySign: getUserCurrencySign(),
      token: '',
      amount: null,
      recipient: this.toAddress,
      required,
      password,
      email,
      min,
      integer,
      url,
      alpha,
      between,
      digits,
      length,
      alphaDash
    };
  },

  computed: {
    msg() {
      return [{
        typeUrl: '/cosmos.bank.v1beta1.MsgSend',
        value: {
          fromAddress: this.address,
          toAddress: this.recipient,
          amount: [{
            amount: getUnitAmount(this.amount, this.token),
            denom: this.token
          }]
        }
      }];
    },

    balanceOptions() {
      return this.setupBalance();
    },

    IBCDenom() {
      return this.$store.state.chains.denoms;
    },

    valuation() {
      const {
        amount
      } = this;

      if (amount) {
        const d2 = this.printDenom();
        const quote = this.$store.state.chains.quotes[d2];
        const price = quote ? quote[this.currency] : 0;
        return parseFloat((amount * price).toFixed(2));
      }

      return 0;
    }

  },

  mounted() {
    this.$emit('update', {
      modalTitle: 'Transfer Tokens',
      historyName: 'send'
    });
  },

  methods: {
    setupBalance() {
      if (this.balance && this.balance.length > 0) {
        this.token = this.balance[0].denom;
        return this.balance;
      }

      return [];
    },

    format(v) {
      return formatToken(v, this.IBCDenom, 6);
    },

    printDenom() {
      return formatTokenDenom(this.IBCDenom[this.token] || this.token);
    }

  }
};
