import { BCardTitle, BCardFooter, BButton, BProgressBar, BProgress, BBadge, BTooltip, BRow, BCol, VBModal } from 'bootstrap-vue';
import Ripple from 'vue-ripple-directive';
import OperationModal from '@/views/components/OperationModal/index.vue';
import ProposalSummaryComponent from './components/governance/ProposalSummaryComponent.vue';
export default {
  components: {
    BButton,
    BCardFooter,
    BProgressBar,
    BProgress,
    BBadge,
    BCardTitle,
    BTooltip,
    BRow,
    BCol,
    OperationModal,
    ProposalSummaryComponent
  },
  directives: {
    'b-modal': VBModal,
    Ripple
  },

  data() {
    return {
      selectedProposalId: 0,
      selectedTitle: '',
      proposals: [],
      max: 1,
      operationModalType: '',
      next: '',
      totalPower: 0,
      tallyParam: null
    };
  },

  mounted() {
    this.$http.getGovernanceParameterTallying().then(res => {
      this.tallyParam = res;
    });
    this.getList();
  },

  methods: {
    getList() {
      this.loading = true;
      this.$http.getGovernanceList(this.next).then(res => {
        this.proposals = this.proposals.concat(res.proposals);
        this.updateTally(this.proposals);
        this.next = res.pagination.next_key;
        this.loading = false;
      });
    },

    updateTally(res) {
      this.$http.getStakingPool().then(pool => {
        this.totalPower = pool.bondedToken;
        const voting = res.filter(i => i.status === 2);

        if (voting.length > 0) {
          voting.forEach(p => this.$http.getGovernanceTally(p.id, 0).then(update => {
            this.$set(p, 'tally', update);
          }));
        }
      });
    }

  }
};
