import { BCard, BAvatar, BMedia, BMediaAside, BMediaBody, VBTooltip } from 'bootstrap-vue';
import ToastificationContent from '@core/components/toastification/ToastificationContent.vue';
export default {
  components: {
    BCard: BCard,
    BAvatar: BAvatar,
    BMedia: BMedia,
    BMediaAside: BMediaAside,
    BMediaBody: BMediaBody,
    // eslint-disable-next-line vue/no-unused-components
    ToastificationContent: ToastificationContent
  },
  directives: {
    'b-tooltip': VBTooltip
  },
  props: {
    operatorAddress: {
      type: String,
      default: '-'
    },
    accountAddress: {
      type: String,
      default: '-'
    },
    consensusPubkey: {
      type: [Object, String],
      required: true
    },
    hexAddress: {
      type: String,
      default: '-'
    }
  },
  methods: {
    copy: function copy(v) {
      var _this = this;

      this.$copyText(v).then(function () {
        _this.$toast({
          component: ToastificationContent,
          props: {
            title: 'Address copied',
            icon: 'BellIcon'
          }
        });
      }, function (e) {
        _this.$toast({
          component: ToastificationContent,
          props: {
            title: "Failed to copy address! ".concat(e),
            icon: 'BellIcon',
            variant: 'danger'
          }
        });
      });
    }
  }
};
