import { VBTooltip, BTable, BAvatar, BButton } from 'bootstrap-vue';
import { getLocalTxHistory } from '@/libs/utils';
export default {
  components: {
    BTable,
    BAvatar,
    BButton
  },
  directives: {
    'b-tooltip': VBTooltip
  },

  data() {
    return {
      fields: [{
        key: 'chain',
        label: 'BLOCKCHAIN'
      }, {
        key: 'op',
        label: 'ACTION'
      }, {
        key: 'hash',
        label: 'TX HASH'
      }, {
        key: 'time',
        label: 'TIME'
      }],
      history: []
    };
  },

  created() {
    this.history = getLocalTxHistory();
  },

  methods: {
    clear() {
      this.history = [];
      localStorage.setItem('txHistory', []);
    }

  }
};
