import { VBTooltip, BTable, BAvatar, BButton } from 'bootstrap-vue';
import { getLocalTxHistory } from '@/libs/utils';
export default {
  components: {
    BTable: BTable,
    BAvatar: BAvatar,
    BButton: BButton
  },
  directives: {
    'b-tooltip': VBTooltip
  },
  data: function data() {
    return {
      fields: [{
        key: 'chain',
        label: 'BLOCKCHAIN'
      }, {
        key: 'op',
        label: 'ACTION'
      }, {
        key: 'hash',
        label: 'TX HASH'
      }, {
        key: 'time',
        label: 'TIME'
      }],
      history: []
    };
  },
  created: function created() {
    this.history = getLocalTxHistory();
  },
  methods: {
    clear: function clear() {
      this.history = [];
      localStorage.setItem('txHistory', []);
    }
  }
};
