import { percent } from '@/libs/utils';
import { $themeColors } from '@themeConfig';
import ChartjsComponentDoughnutChart from './ChartjsComponentDoughnutChart';
export default {
  name: 'ChartDoughnut',
  components: {
    ChartjsComponentDoughnutChart: ChartjsComponentDoughnutChart
  },
  props: {
    height: {
      type: Number,
      default: 235
    },
    width: {
      type: Number,
      default: 235
    },
    data: {
      type: Object,
      required: true
    }
  },
  data: function data() {
    return {
      options: {
        responsive: true,
        maintainAspectRatio: false,
        responsiveAnimationDuration: 500,
        cutoutPercentage: 60,
        legend: {
          display: true,
          title: {
            display: true
          }
        },
        tooltips: {
          callbacks: {
            label: function label(tooltipItem, data) {
              var label = data.datasets[0].labels[tooltipItem.index] || '';
              var value = data.datasets[0].data[tooltipItem.index];
              var total = data.datasets[0].data.reduce(function (t, c) {
                return t + c;
              });
              var output = " ".concat(label, " : ").concat(percent(value / total), " %");
              return output;
            }
          },
          // Updated default tooltip UI
          shadowOffsetX: 1,
          shadowOffsetY: 1,
          shadowBlur: 8,
          // shadowColor: chartColors.tooltipShadow,
          backgroundColor: $themeColors.light,
          titleFontColor: $themeColors.dark,
          bodyFontColor: $themeColors.dark
        }
      }
    };
  }
};
