import { BCard, BAvatar, BMedia, BMediaAside, BMediaBody, VBTooltip } from 'bootstrap-vue';
import ToastificationContent from '@core/components/toastification/ToastificationContent.vue';
export default {
  components: {
    BCard,
    BAvatar,
    BMedia,
    BMediaAside,
    BMediaBody,
    // eslint-disable-next-line vue/no-unused-components
    ToastificationContent
  },
  directives: {
    'b-tooltip': VBTooltip
  },
  props: {
    operatorAddress: {
      type: String,
      default: '-'
    },
    accountAddress: {
      type: String,
      default: '-'
    },
    consensusPubkey: {
      type: [Object, String],
      required: true
    },
    hexAddress: {
      type: String,
      default: '-'
    }
  },
  methods: {
    copy(v) {
      this.$copyText(v).then(() => {
        this.$toast({
          component: ToastificationContent,
          props: {
            title: 'Address copied',
            icon: 'BellIcon'
          }
        });
      }, e => {
        this.$toast({
          component: ToastificationContent,
          props: {
            title: `Failed to copy address! ${e}`,
            icon: 'BellIcon',
            variant: 'danger'
          }
        });
      });
    }

  }
};
